import React, { useCallback, useMemo } from 'react';
import { ActivityIndicator, Image, StyleSheet, Text, View } from 'react-native';

import { TouchableOpacity } from 'react-native-gesture-handler';
import type { ToastProperties } from '../Toast/typings';
import { useToast } from '../../hooks/useToast';
import { Swipeable } from '../Swipeable';
import { useAutoHide } from '../../hooks/useAutoHide';

const ToastColors = {
  default: '#6d6d6d',
  success: '#42d886',
  error: '#f94416',
  info: '#0387d8',
  warning: '#febc00',
};

const ToastIcons = {
  success:
    'data:image/png;base64,',
  info: 'data:image/png;base64,',
  warning:
    'data:image/png;base64,',
  error:
    'data:image/png;base64,',
};

export const Toast = () => {
  const {
    hide,
    loading,
    type = 'default',
    dismissible = true,
    onPress,
    text,
  } = useToast<ToastProperties>();
  useAutoHide();

  const _onPress = useCallback(() => {
    if (onPress) onPress();
    if (dismissible) hide();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [onPress, dismissible]);

  return useMemo(
    () => (
      <Swipeable onSwipe={hide} disabled={loading || !dismissible}>
        <View style={styles.container}>
          <TouchableOpacity
            activeOpacity={0.8}
            disabled={loading || !dismissible}
            style={[
              styles.touchable,
              {
                backgroundColor: ToastColors[type],
              },
            ]}
            onPress={_onPress}
          >
            {type !== 'default' && (
              <Image
                source={{ uri: ToastIcons[type] }}
                resizeMode="contain"
                style={styles.icon}
              />
            )}
            <Text style={styles.text}>{text}</Text>
            {loading && <ActivityIndicator size="small" color="#fff" />}
          </TouchableOpacity>
        </View>
      </Swipeable>
    ),
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [loading]
  );
};

const styles = StyleSheet.create({
  container: {
    paddingHorizontal: 10,
    paddingVertical: 2,
  },
  touchable: {
    alignItems: 'center',
    flexDirection: 'row',
    borderRadius: 5,
    padding: 10,
    minHeight: 40,
  },
  text: {
    color: '#ffffff',
    flex: 1,
  },
  icon: {
    width: 20,
    height: 20,
    marginRight: 10,
  },
});
